    <Sidebar v-if="!isHiddenSidebarPage" />
    <div :class="['main-content', { 'no-sidebar': isHiddenSidebarPage }]">
      <router-view />
    </div>
    <Footer />
  </div>
</template>

<script>
import Header from './components/Header.vue';
import Sidebar from './components/Sidebar.vue';
import Footer from './components/Footer.vue';
import { computed } from 'vue';
import { useRoute } from 'vue-router';

export default {
  components: {
    Header,
    Sidebar,
    Footer,
  },
  setup() {
    const route = useRoute();
    const isHiddenSidebarPage = computed(() => route.name === 'Home' || route.name === 'Login');
    return {
      isHiddenSidebarPage,
    };
  },
};
</script>

