### 更新后的 `UserService` 实现

```java
// src/main/java/org/mua/service/UserService.java
package org.mua.service;

import org.mua.model.User;
import org.mua.model.EnrolledStudent;
import org.mua.repository.UserRepository;
import org.mua.repository.EnrolledStudentRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import java.util.Optional;

@Service
public class UserService {

    @Autowired
    private UserRepository userRepository;

    @Autowired
    private EnrolledStudentRepository enrolledStudentRepository;

    /**
     * 注册用户前，检查在籍学生的资格
     */
    public User registerUser(User user, String idNumber) {
        // 检查用户是否在 enrolled_students 表中
        Optional<EnrolledStudent> enrolledStudent = enrolledStudentRepository
                .findByUsernameAndIdNumber(user.getUsername(), idNumber);

        if (enrolledStudent.isEmpty()) {
            throw new RuntimeException("注册失败：用户不在在籍学生名单中");
        }

        // 检查用户名和邮箱是否已存在
        if (userRepository.existsByUsername(user.getUsername()) || userRepository.existsByEmail(user.getEmail())) {
            throw new RuntimeException("用户名或邮箱已存在");
        }

        // 直接保存明文密码
        return userRepository.save(user);
    }

    /**
     * 通过用户名查找用户
     */
    public Optional<User> findByUsername(String username) {
        return userRepository.findByUsername(username);
    }

    /**
     * 校验用户的用户名和明文密码
     */
    public boolean validateUser(String username, String password) {
        Optional<User> user = userRepository.findByUsername(username);
        return user.isPresent() && password.equals(user.get().getPassword());
    }
}
