package org.mua.service;

import org.mua.model.User;
import org.mua.repository.UserRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import java.util.Optional;

@Service
public class UserService {

    @Autowired
    private UserRepository userRepository;

    public User registerUser(User user) {
        if (userRepository.existsByUsername(user.getUsername()) || userRepository.existsByEmail(user.getEmail()) || userRepository.existsByIdNumber(user.getIdNumber())) {
            throw new RuntimeException("Username, Email, or ID Number already exists");
        }
        return userRepository.save(user);
    }

    public Optional<User> findByUsername(String username) {
        return userRepository.findByUsername(username);
    }

    /**
     * 验证用户的登录信息
     *
     * @param emailOrIdNumber 用户输入的邮箱或身份证号码
     * @param password 用户输入的密码
     * @return true 表示验证成功，false 表示失败
     */
    public boolean validateUser(String emailOrIdNumber, String password) {
        Optional<User> user;

        if (emailOrIdNumber.contains("@")) {
            // 通过邮箱查找用户
            user = userRepository.findByEmail(emailOrIdNumber);
        } else {
            // 通过身份证号查找用户
            user = userRepository.findByIdNumber(emailOrIdNumber);
        }

        // 检查用户是否存在并匹配密码
        return user.isPresent() && password.equals(user.get().getPassword());
    }
}
