  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.1);
  z-index: 1051;
  opacity: 0; /* 初始设置为不可见 */
  transform: translate(-50%, 10px); /* 初始位置稍微向下 */
  transition: opacity 0.3s ease, transform 0.3s ease; /* 设置动画 */
}

.auth-links:hover .dropdown-options {
  opacity: 1; /* 鼠标悬停时显示 */
  transform: translate(-50%, 0); /* 恢复位置 */
}
